const DECISION_FACTS_FR = ``;

/**
 * OPTIONAL: Paste cited legal provisions here
 */
const DECISION_CITED_PROVISIONS = ``;

async function testDecision() {
  try {
    console.log('\n=== Testing Decision 110933 ===\n');

    // 1. Fetch decision from database
    console.log('📥 Fetching decision from database...');
    const db = DatabaseConfig.getPool();

    const result = await db.query(
      `SELECT id, decision_id, language_metadata
       FROM decisions1
       WHERE id = $1`,
      [110938]
    );

    if (result.rows.length === 0) {
      console.error('❌ Decision not found!');
      process.exit(1);
    }

    const decision = result.rows[0];
    console.log('✅ Decision found:');
    console.log(`   ID: ${decision.id}`);
    console.log(`   Decision ID: ${decision.decision_id}`);
    console.log(`   Language: ${decision.language_metadata}`);

    // 2. Load taxonomy
    console.log('\n📚 Loading taxonomy...');
    await TaxonomyManager.loadTaxonomy();
    const stats = TaxonomyManager.getStats();
    console.log(`✅ Taxonomy loaded: ${stats.totalEntries} entries, ${stats.parentCategories} parent categories`);

    // 3. Get parent categories
    const parentCategories = TaxonomyManager.getParentCategories();
    console.log(`\n📋 Available parent categories:`);
    parentCategories.forEach(cat => {
      console.log(`   ${cat.id}: ${cat.nameFr}`);
    });

    // 4. Run taxonomy filter (GPT-4o category selection - selects exactly 4 categories)
    console.log('\n🤖 Running taxonomy filter (GPT-4o)...');

    // Use hardcoded full text if available, otherwise use decision_id as proxy
    const decisionText = DECISION_FULL_TEXT_MARKDOWN.trim().length > 100
      ? DECISION_FULL_TEXT_MARKDOWN
      : `Decision ${decision.decision_id} - Language: ${decision.language_metadata}`;

    console.log(`   Using decision text: ${decisionText.length} characters`);
    if (decisionText.length <= 100) {
      console.log('   ⚠️  WARNING: Using decision_id as proxy (full text not provided)');
    }

    let selectedCategoryIds: string[];
    try {
      selectedCategoryIds = await TaxonomyFilterService.selectRelevantCategories(
        decisionText,
        parentCategories
      );

      console.log(`✅ GPT-4o selected ${selectedCategoryIds.length} categories:`);
      selectedCategoryIds.forEach(id => {
        const cat = parentCategories.find(c => c.id === id);
        console.log(`   ✓ ${id}: ${cat?.nameFr}`);
      });
    } catch (error) {
      console.error('❌ Taxonomy filter failed:', error);
      process.exit(1);
    }

    // 5. Filter taxonomy to selected 4 categories
    console.log('\n🔍 Filtering taxonomy to selected 4 categories...');
    const filteredTaxonomy = TaxonomyManager.filterByParents(selectedCategoryIds);
    console.log(`✅ Filtered taxonomy: ${filteredTaxonomy.length} entries`);

    const language = decision.language_metadata?.toLowerCase() === 'nl' ? 'nl' : 'fr';

    // 6. Format taxonomy for prompt
    const taxonomyString = TaxonomyManager.formatForPrompt(filteredTaxonomy, language);
    console.log(`\n📝 Formatted taxonomy for prompt (first 500 chars):`);
    console.log('─'.repeat(80));
    console.log(taxonomyString.substring(0, 500) + '...');
    console.log('─'.repeat(80));

    // 7. Prepare full prompt (what would be sent to Azure)
    console.log('\n📄 Preparing keyword generation prompt...');

    const enrichedRow = {
      id: decision.id,
      decision_id: decision.decision_id,
      language_metadata: decision.language_metadata,
      keywordsUtu: taxonomyString,
      fullTextMarkdown: DECISION_FULL_TEXT_MARKDOWN.trim().length > 100 ? DECISION_FULL_TEXT_MARKDOWN : '',
      factsFr: DECISION_FACTS_FR,
      citedProvisions: DECISION_CITED_PROVISIONS,
      proceduralLanguage: decision.language_metadata || 'FR',
    };

    const fullPrompt = KEYWORDS_PROMPT
      .replace('{{decisionId}}', enrichedRow.id?.toString() || '')
      .replace('{{fullTextMarkdown}}', enrichedRow.fullTextMarkdown || '')
      .replace('{{factsFr}}', enrichedRow.factsFr || '')
      .replace('{{citedProvisions}}', enrichedRow.citedProvisions || '')
      .replace('{{proceduralLanguage}}', enrichedRow.proceduralLanguage || 'FR')
      .replace('{{keywordsUtu}}', enrichedRow.keywordsUtu || '');

    console.log(`✅ Prompt prepared (${fullPrompt.length} characters)`);
    console.log(`\nPrompt structure:`);
    console.log(`   - Total length: ${fullPrompt.length} chars`);
    console.log(`   - Taxonomy section: ${taxonomyString.length} chars`);
    console.log(`   - Full text: ${enrichedRow.fullTextMarkdown.length} chars${enrichedRow.fullTextMarkdown.length === 0 ? ' (empty - not provided)' : ''}`);
    console.log(`   - Facts (FR): ${enrichedRow.factsFr.length} chars${enrichedRow.factsFr.length === 0 ? ' (empty - not provided)' : ''}`);
    console.log(`   - Cited provisions: ${enrichedRow.citedProvisions.length} chars${enrichedRow.citedProvisions.length === 0 ? ' (empty - not provided)' : ''}`);

    // 8. Call OpenAI to generate keywords (using personal account for testing)
    console.log('\n🤖 Calling OpenAI to generate keywords...');
    console.log('   Model: gpt-4o');
    console.log('   Temperature: 0.0');
    console.log('   Max tokens: 4000');

    try {
      const openaiClient = OpenAIConfig.getClient();

      const response = await openaiClient.chat.completions.create({
        model: 'gpt-4o',
        messages: [
          {
            role: 'user',
            content: fullPrompt,
          },
        ],
        temperature: 0.0,
        max_tokens: 4000,
        response_format: { type: 'json_object' },
      });

      const content = response.choices[0]?.message?.content;
      if (!content) {
        console.error('❌ No response from OpenAI');
        process.exit(1);
      }

      console.log('✅ Response received!\n');

      // Parse the JSON response
      const result = JSON.parse(content);

      // Show the complete output
      console.log('📋 COMPLETE JSON OUTPUT:');
      console.log('═'.repeat(80));
      console.log(JSON.stringify(result, null, 2));
      console.log('═'.repeat(80));

      // Extract and highlight the KUD IDs selected
      console.log('\n🎯 SELECTED KUD IDS:');
      console.log('─'.repeat(80));
      if (result.index?.legalIssues && Array.isArray(result.index.legalIssues)) {
        result.index.legalIssues.forEach((issue: any, idx: number) => {
          console.log(`${idx + 1}. [${issue.id}] ${issue.keywordsSequenceFr || issue.keywordsSequenceNl}`);
        });
        console.log('─'.repeat(80));
        console.log(`Total KUDs selected: ${result.index.legalIssues.length}`);
      } else {
        console.log('⚠️  No legalIssues found in response');
      }

      // Show custom keywords
      if (result.index?.customKeywords && Array.isArray(result.index.customKeywords)) {
        console.log('\n📝 CUSTOM KEYWORDS:');
        console.log('─'.repeat(80));
        result.index.customKeywords.forEach((keyword: string, idx: number) => {
          console.log(`${idx + 1}. ${keyword}`);
        });
        console.log('─'.repeat(80));
        console.log(`Total custom keywords: ${result.index.customKeywords.length}`);
      }

      // Show metadata
      if (result.metadata) {
        console.log('\n📊 METADATA:');
        console.log('─'.repeat(80));
        console.log(JSON.stringify(result.metadata, null, 2));
        console.log('─'.repeat(80));
      }

    } catch (error) {
      console.error('❌ OpenAI call failed:', error);
      process.exit(1);
    }

    console.log('\n✅ Test completed successfully!\n');
    console.log('Summary:');
    console.log(`   • Decision: ${decision.decision_id} (ID: ${decision.id})`);
    console.log(`   • Language: ${language}`);
    console.log(`   • Selected categories: ${selectedCategoryIds.join(', ')}`);
    console.log(`   • Filtered taxonomy: ${filteredTaxonomy.length} entries (from ${stats.totalEntries} total)`);
    console.log(`   • Reduction: ${((1 - filteredTaxonomy.length / stats.totalEntries) * 100).toFixed(1)}%`);
    console.log(`   • Final prompt: ${fullPrompt.length} characters\n`);

    process.exit(0);
  } catch (error) {
    console.error('\n❌ Test failed:', error);
    process.exit(1);
  }
}

// Run test
testDecision();
